function makeRelative( el ) {

	var parent		= el.parent(),
		parentH		= parent.height(),
		thisH		= el.height();

	if( parentH >= thisH ) {
		parent.removeClass('adapt-to-child');
	}else
	if( parentH < thisH) {
		parent.addClass('adapt-to-child');
	}

}
